const requiredFields = [{
  field: 'nameInput',
  error: ERROR_MESSAGE.NAME_REQUIRED
}, {
  field: 'lastNameInput',
  error: ERROR_MESSAGE.LAST_NAME_REQUIRED
}, {
  field: 'emailInput',
  error: ERROR_MESSAGE.EMAIL_REQUIRED
}, {
  field: 'passwordInput',
  error: ERROR_MESSAGE.PASSWORD_REQUIRED
}, {
  field: 'repeatPasswordInput',
  error: ERROR_MESSAGE.RE_ENTER_PASSWORD_REQUIRED
}];
requiredFields.forEach(({
  field,
  error
}) => {
  _registrationModal.default[field].click();
  _registrationModal.default.title.click();
  _registrationModal.default.error(error).should('be.visible');
  _registrationModal.default[field].should('have.css', 'border-color', 'rgb(220, 53, 69)');
});
_registrationModal.default.registerButton.should('be.disabled');

const fieldsWithSpaces = [{
  input: 'nameInput',
  invalidValues: ['Ok sa na', 'Оксана', 12345, 'ok-sa*na', '@123Qwe'],
  valid: 'Ok',
  error: ERROR_MESSAGE.INVALID_NAME
}, {
  input: 'lastNameInput',
  invalidValues: ['Bak h ma tska', 'Бахмацька', 12345, 'ok-sa*na', '@123Qwe'],
  valid: 'Ba',
  error: ERROR_MESSAGE.INVALID_LAST_NAME
}];
fieldsWithSpaces.forEach(({
  input,
  invalidValues,
  valid,
  error
}) => {
  invalidValues.forEach(invalid => {
    _registrationModal.default[input].clear().type(invalid);
    _registrationModal.default.title.click();
    _registrationModal.default.error(error).should('be.visible');
    _registrationModal.default[input].should('have.css', 'border-color', 'rgb(220, 53, 69)');
  });
  _registrationModal.default[input].clear().type(valid);
  _registrationModal.default.title.click();
  _registrationModal.default.error(error).should('not.exist');
  _registrationModal.default[input].should('have.css', 'border-color').and('not.eq', 'rgb(220, 53, 69)');
});
_registrationModal.default.registerButton.should('be.disabled');

